import React, { useState, useCallback, createContext, ReactNode, Dispatch, SetStateAction, } from "react";
import axios from "axios";
import "./style.css";
import { Todo } from "../types/Todo";

const APP_URL = "http://localhost:8080/todo";

interface AppContextType {
  isLoggedIn: boolean;
  login: () => void;
  logout: () => void;
  tasks: Todo[];
  setTasks: Dispatch<SetStateAction<Todo[]>>;
  task: string;
  setTask: Dispatch<SetStateAction<string>>;
  addTask: () => void;
  deleteTask: (index: number) => void;
  toggleTask: (index: number) => void;
}

export const AppContext = createContext<AppContextType>({} as AppContextType);

interface Props {
  children: ReactNode;
}

const AppContextProvider: React.FC<Props> = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(() => {
    return localStorage.getItem("isLoggedIn") === "true";
  });

  const [tasks, setTasks] = useState<Todo[]>([]);
  const [task, setTask] = useState<string>("");

  const login = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };

  const logout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
  };

  const addTask = useCallback(() => {
    if (task.trim()) {
      axios
        .post(APP_URL, { name: task, completed: false })
        .then((response) => {
          setTasks([...tasks, response.data]);
          setTask("");
        })
        .catch((error) => console.error("Error adding task:", error));
    }
  }, [task, tasks]);

  const deleteTask = useCallback(
    (index: number) => {
      axios
        .delete(`${APP_URL}/${index}`)
        .then(() => {
          setTasks(tasks.filter((_, i) => i !== index));
        })
        .catch((error) => console.error("Error deleting task:", error));
    },
    [tasks]
  );

  const toggleTask = useCallback(
    (index: number) => {
      const updatedTasks = [...tasks];
      updatedTasks[index].completed = !updatedTasks[index].completed;
      setTasks(updatedTasks);
    },
    [tasks]
  );

  return (
    <AppContext.Provider
      value={{
        isLoggedIn,
        login,
        logout,
        tasks,
        setTasks,
        task,
        setTask,
        addTask,
        deleteTask,
        toggleTask,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export default AppContextProvider;
